package Java_Assignment2;

public class OddNumbers79To187 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		{                   
		    int n = 187;                       
		    System.out.print("Odd Numbers from 79 to "+n+" are: ");                       
		    for (int i = 79; i <= n; i++) {                       
		       if (i % 2 != 0) {                       
		        System.out.print(i + " ");                   
		       }                       
		    }     
		    
		   }     
		
	}

}
